actionConfiguration.body:
() => {
  const missionType = dropdown_mission.selectedOptionValue;
  let details = {};
  if (missionType === "Deploy Drone Surveillance Unit") {
    details = {
      drone_id: input1.text,
      operation_area: input2.text,
      payload: input3.text
    };
  } else if (missionType === "Set Up Secure Communications Relay") {
    details = {
      relay_id: input1.text,
      channel: input2.text,
      encryption_protocol: input3.text
    };
  } else if (missionType === "Sync Classified Intelligence Database") {
    details = {
      database_name: input1.text,
      sync_frequency: input2.text,
      transmission_channel: input3.text
    };
  }
  return {
    agent_id: input_agentId.text,
    mission: missionType,
    details: details
  };
}

